<template>
  <div id="app">
    <icon-bar />
    <file-tree :tree-data="fileTree" :models="models" class="file-tree" />
    <div ref="editor" class="editor" id="container"></div>
  </div>
</template>

<script>
import IconBar from './components/IconBar.vue';
import FileTree from './components/FileTree.vue';
import * as monaco from 'monaco-editor';
import _ from 'lodash';

export default {
  name: 'App',
  components: {
    IconBar,
    FileTree,
  },
  data: () => ({
    fileTree: {
      children: [
        {
          id: 1,
          name: 'Main.java',
          model: {
            value: '// Here goes the your main class foobar..',
            language: 'java'
          },
        },
        {
          id: 2,
          name: 'Other.java',
          model: {
            value: '// Enter your code foobar...',
            language: 'java'
          },
        },
        {
          name: 'some_dir',
          children: [
            {
              id: 3,
              name: 'Hello.java',
              model: {
                value: '// Hello from Hello.java foobar...',
                language: 'java'
              },
            },
            {
              id: 4,
              name: 'World.java',
              model: {
                value: '// Hello from World.java foobar...',
                language: 'java'
              },
            },
          ],
        },
        {
          id: 5,
          name: 'Bar.java',
          model: {
            value: '// Hello from Bar.java...',
            language: 'java'
          },
        },
      ],
    },
    /*fileTree: {
      children: [
        {
          id: 1,
          name: 'Main.java',
          model: {
            value: '// Here goes your main...',
            language: 'java'
          },
        },
      ],
    },*/
    editor: null,
    activeNode: null,
    models: [],
    fileIndexStack: [],
  }),
  methods: {
    createModels(files) {
      let currFiles = files;
      let currIndex = 0;
      this.fileIndexStack.unshift({
        files: currFiles,
        index: currIndex,
      });

      while (true) {
        // Get top of stack
        let topStack = this.fileIndexStack.shift();
        let currFiles = topStack.files;
        let currIndex = topStack.index;

        // Get file
        let file = currFiles[currIndex];

        // Check if directory
        if (file.children) {
          // Add the directory on stack,
          // to use after iterating over every file in folder
          this.fileIndexStack.unshift({
            files: currFiles,
            index: currIndex,
          });

          // Add its first children on to the stack
          currFiles = file.children;
          currIndex = 0;
          this.fileIndexStack.unshift({
            files: currFiles,
            index: currIndex,
          });

          // Start next iteration, because this one is a directory
          continue;
        }

        // Create Model
        let model = {
          id: file.id,
          name: file.name,
          value: file.model.value,
          language: file.model.language,
        };
        this.models.push(model);

        // Check if next file exists
        currIndex++;

        if (currIndex < currFiles.length) {
          this.fileIndexStack.unshift({
            files: currFiles,
            index: currIndex,
          });
        }
        else {
          // If no more files on stack, finished
          if (this.fileIndexStack.length <= 0) {
            break;
          }

          // We created models for all files in directory
          // Remove directory from top of the stack
          topStack = this.fileIndexStack.shift();
          currFiles = topStack.files;
          currIndex = topStack.index;

          // Add next file on top of the stack
          currIndex++;
          this.fileIndexStack.unshift({
            files: currFiles,
            index: currIndex,
          });
        }
      }
    },
    storeModelUpdate: _.debounce(function(_) {
      let newModel = {
        id: this.$store.getters.activeNode.id,
        name: this.$store.getters.activeNode.name,
        language: this.$store.getters.activeNode.model.language,
        value: this.editor.getValue(),
      };

      for (let i = 0; i < this.models.length; i++) {
        if (this.models[i].id === newModel.id) {
          this.models[i] = newModel;
          break;
        }
      }

      this.$store.dispatch('updateModels', this.models);
      console.log(this.$store.getters.models);
    }, 500),
  },
  computed: {
  },
  watch: {
    '$store.state.activeNodeId': function() {
      let activeModel;

      for (let i = 0; i < this.models.length; i++) {
        if (this.models[i].id === this.$store.state.activeNodeId) {
          activeModel = this.models[i];
          break;
        }
      }

      this.editor.setModel(
        monaco.editor.createModel(
          activeModel.value,
          activeModel.language,
        )
      );
    },
  },
  mounted() {
    this.createModels(this.fileTree.children);

    this.editor = monaco.editor.create(document.getElementById('container'), {
      language: 'java'
    });

    // Set main-file as active model
    let mainModel = monaco.editor.createModel(
      this.models[0].value,
      this.models[0].language,
    );
    //this.$store.dispatch('updateActiveModel', this.models[0]);
    this.editor.setModel(mainModel);
    this.$store.dispatch('updateActiveNode', this.fileTree.children[0]);

    //let { activeNode } = this.$store.getters.activeNode;
    this.activeNode = this.$store.getters.activeNode;

    // Set editor notifiers
    this.editor.onDidChangeModelContent(_ => {
      this.storeModelUpdate();
    });
  },
}
</script>

