<template>
  <div class="tree">
    <div class="tree-header">
      FILES
    </div>

    <ul class="tree-list">
      <node-tree :node="treeData"></node-tree>
    </ul>
    <div class="new-file-btn" @click="addNewFile">
      <span>+ NEW FILE</span>
    </div>
  </div>
</template>

<script>
import NodeTree from "./NodeTree";

export default {
  name: 'file-tree',
  props: {
    treeData: Object,
    models: Array,
  },
  components: {
    NodeTree
  },
  data: function() {
    return {
      newFileCounter: 1,
    };
  },
  methods: {
    addNewFile() {
      let file = {
        id: this.$props.treeData.children.length + 1,
        name: 'NewFile' + (this.newFileCounter++) + '.java',
        model: {
          value: '// Enter your code here...',
          language: 'java'
        },
      };

      // Create Model
      let model = {
        id: file.id,
        name: file.name,
        value: file.model.value,
        language: file.model.language,
      };
      this.models.push(model);

      this.$props.treeData.children.push(file);
    },
  },
};
</script>

